Bash(echo "/**
 * Better Auth configuration for frontend authentication
 */
import { createAuthClient } from ""better-auth/react"";

// Initialize Better Auth client
const authClient = createAuthClient\({
  baseURL: process.env.NEXT_PUBLIC_API_BASE_URL || ""http://localhost:8000"",
  fetch: globalThis.fetch,
}\);

// Export individual auth methods
export const { signIn, signOut, useSession } = authClient;

// Type definitions for user session
export interface UserSession {
  user: {
    id: string;
    email: string;
    name?: string;
  };
  access_token: string;
}

// Function to store JWT token in localStorage
export const storeJwtToken = \(token: string\): void => {
  if \(typeof window !== ''undefined''\) {
    localStorage.setItem\(''access_token'', token\);
  }
};

// Function to retrieve JWT token from localStorage
export const retrieveJwtToken = \(\): string | null => {
  if \(typeof window !== ''undefined''\) {
    return localStorage.getItem\(''access_token''\);
  }
  return null;
};

// Function to remove JWT token from storage
export const removeJwtToken = \(\): void => {
  if \(typeof window !== ''undefined''\) {
    localStorage.removeItem\(''access_token''\);
  }
};

// Function to get JWT token from storage for API calls
export const getJwtToken = \(\): string | null => {
  // Get token from localStorage as the primary source
  let token = retrieveJwtToken\(\);
  return token;
};

// Function to check if user is authenticated
export const isAuthenticated = \(\): boolean => {
  // Check if we have a token in localStorage
  return !!retrieveJwtToken\(\);
};

// Export the auth client for direct use if needed
export default authClient;")
